  //       setImages(
  //         response.data.map(url => ({
  //           original: `${url}=w1024`,
  //           thumbnail: `${url}=w100`
  //         }))
  //       );
  //     }
  //   };
  //   call();
  //   return () => (shouldCancel = true);
//   }, []);

  return images; //? <ImageGallery items={images} /> : null;
};

const rootElement = document.getElementById("root");
ReactDOM.render(<ImagesGallery />, rootElement);
